        // }
        const marginMode = this.safeString(data, 'marginMode');
        const isIsolated = (marginMode === 'fixed');
        let leverage = this.safeFloat(data, 'crossMarginLeverage');
        const buyLeverage = this.safeFloat(data, 'fixedLongLeverage');
