        $side = $this->safe_string($trade, 'side');
        $orderId = $this->safe_string($trade, 'ordId');
        $feeCostString = $this->safe_string($trade, 'fee');
        $fee = null;
        if ($feeCostString !== null) {
            $feeCostSigned = Precise::string_neg($feeCostString);
            $feeCurrencyId = $this->safe_string($trade, 'feeCurrency');
            $feeCurrencyCode = $this->safe_currency_code($feeCurrencyId);
            if ($feeCurrencyCode === null) {
                $feeCurrencyCode = 'USDT';
            }
            $fee = array(
                'cost' => $feeCostSigned,
                'currency' => $feeCurrencyCode,
            );
        }
        $takerOrMaker = $this->safe_string($trade, 'execType');
        if ($takerOrMaker === 'T') {
            $takerOrMaker = 'taker';
        } elseif ($takerOrMaker === 'M') {
            $takerOrMaker = 'maker';
        }
        return $this->safe_trade(array(
            'info' => $this->deep_extend($trade, array( 'symbol' => $marketId )),
            'timestamp' => $timestamp,
            'datetime' => $this->iso8601($timestamp),
            'symbol' => $symbol,
            'id' => $id,
            'order' => $orderId,
            'type' => null,
            'takerOrMaker' => $takerOrMaker,
            'side' => $side,
            'price' => $price,
            'amount' => $amount,
            'cost' => null,
            'fee' => $fee,
        ), $market);
    }

    public function parse_token_and_fee_temp($item, $feeTokenKey, $feeAmountKey) {
        $feeCost = $this->safe_string($item, $feeAmountKey);
        $fee = null;
        if ($feeCost !== null) {
            $feeCurrencyId = $this->safe_string($item, $feeTokenKey);
            $feeCurrencyCode = $this->safe_currency_code($feeCurrencyId);
            $fee = array(
                'cost' => $feeCost,
                'currency' => $feeCurrencyCode,
            );
        }
        return $fee;
    }

    public function create_order($symbol, $type, $side, $amount, $price = null, $params = array ()) {
        return Async\async(function () use ($symbol, $type, $side, $amount, $price, $params) {
            Async\await($this->load_markets());
            $market = $this->market($symbol);
            // TEALSTREET
            $reduceOnly = $this->safe_value_2($params, 'reduceOnly', 'close');
            $timeInForces = array(
                'GTC' => 'GTC',
                'PO' => 'post_only',  // good till crossing
                'IOC' => 'ioc',
                'FOK' => 'fok',
            );
            $orderTypes = array(
                'market' => 'market',
                'limit' => 'limit',
                'stop' => 'conditional',
                'stoplimit' => 'trigger',
                'stopLimit' => 'trigger',
                'PO' => 'post_only',
                'FOK' => 'fok',
                'IOC' => 'ioc',
                'optimal_limit_ioc' => 'optimal_limit_ioc',
            );
            $timeInForce = $this->safe_string($timeInForces, $params['timeInForce'], $this->capitalize($params['timeInForce']));
            $orderType = $this->safe_string($orderTypes, $type, $this->capitalize($type));
            if ($timeInForce && $orderType !== 'conditional' && $orderType !== 'trigger') {
                if ($timeInForce === 'post_only' || $timeInForce === 'fok' || $timeInForce === 'ioc') {
                    $orderType = $timeInForce;
                }
            }
            $closeOnTrigger = $this->safe_value($params, 'closeOnTrigger', false);
            $stopPrice = null;
            if ($orderType === 'conditional' || $orderType === 'trigger') {
                $stopPrice = $this->safe_number($params, 'stopPrice');
                if ($closeOnTrigger) {
                    $reduceOnly = true;
                }
                $params = $this->omit($params, array( 'reduceOnly' ));
            }
            $side = strtolower($side);
            $posSide = null;
            if (($side === 'buy' && $reduceOnly) || ($side === 'sell' && !$reduceOnly)) {
                $posSide = 'short';
            } else {
                $posSide = 'long';
            }
            $marginType = $this->safe_string($params, 'marginMode', 'cross');
            $method = 'v1PrivatePostTradeOrder';
            if ($type === 'stop' || $type === 'stopLimit') {
                $method = 'v1PrivatePostTradeOrderTpsl';
            }
            $request = array(
                'instId' => $market['id'],
                'marginMode' => $marginType,
                'side' => $side,
                // $posSide is not used by blofin
                'orderType' => $orderType,
                'reduceOnly' => $reduceOnly,
            );
            $params = $this->omit($params, array( 'clientOrderId' ));
            if ($price !== null) {
                $request['price'] = $this->price_to_precision($symbol, $price);
            }
            $request['size'] = $this->amount_to_precision($symbol, $amount);
            if (!$reduceOnly) {
                $request = $this->omit($request, array( 'reduceOnly' ));
            }
            if ($orderType === 'conditional' || $orderType === 'trigger') {
                // unused by blofin right now
                // $triggerType = $this->safe_string_lower($params, 'trigger', 'mark');
                $params = $this->omit($params, array( 'trigger' ));
                // if ($price !== null) {
                //     $price = -1;
                // } else {
                //     $orderType = 'conditional';
                //     $request['orderType'] = $orderType;
                // }
                $basePrice = $this->safe_value($params, 'basePrice');
                if (!$basePrice) {
                    $ticker = $this->fetch_ticker($symbol);
                    $basePrice = $ticker['last'];
                }
                // $tpPrice = $this->safe_number($params, 'tpPrice');
                $tpPrice = null;
                if ($stopPrice > $basePrice) {
                    $tpPrice = $stopPrice;
                }
                if ($tpPrice || $stopPrice) {
                    if ($price === null) {
                        $request['orderType'] = 'oco';
                        if ($tpPrice) {
                            $request['tpTriggerPrice'] = $this->price_to_precision($symbol, $tpPrice);
                            $request['tpOrderPrice'] = $this->price_to_precision($symbol, -1);
                        } elseif ($stopPrice) {
                            $request['slTriggerPrice'] = $this->price_to_precision($symbol, $stopPrice);
                            $request['slOrderPrice'] = $this->price_to_precision($symbol, -1);
                        }
                        // $request['price'] = $this->price_to_precision($symbol, $price);
                        $request['reduceOnly'] = 'true';
                    } else {
                        // this is from our okx $code, but I think second case should be swapped
                        if ($side === 'sell') {
                            if ($stopPrice > $basePrice) {
                                $request['tpTriggerPrice'] = $this->price_to_precision($symbol, $tpPrice);
                                $request['tpOrderPrice'] = $this->price_to_precision($symbol, $price);
                            } else {
                                $request['slTriggerPrice'] = $this->price_to_precision($symbol, $stopPrice);
                                $request['slOrderPrice'] = $this->price_to_precision($symbol, $price);
                            }
                        } else {
                            if ($stopPrice < $basePrice) {
                                $request['tpTriggerPrice'] = $this->price_to_precision($symbol, $tpPrice);
                                $request['tpOrderPrice'] = $this->price_to_precision($symbol, $price);
                            } else {
                                $request['slTriggerPrice'] = $this->price_to_precision($symbol, $stopPrice);
                                $request['slOrderPrice'] = $this->price_to_precision($symbol, $price);
                            }
                        }
                        $request['orderType'] = 'conditional';
                        // $request['price'] = $this->price_to_precision($symbol, -1);
                        // unsupported?
                        // $request['triggerPrice'];
                        $request['reduceOnly'] = 'true';
                    }
                    $request['price'] = -1;
                }
                $request['positionSide'] = $posSide;
            }
            $tradeMode = $this->safe_string($params, 'tradeMode', 'hedged');
            $params = array();
            if ($tradeMode) {
                $params = $this->omit($params, array( 'tradeMode' ));
                if ($tradeMode === 'oneway') {
                    $request = $this->omit($request, array( 'positionSide' ));
                }
                // not implement for blofin yet
                // if ($tradeMode === 'oneway') {
                //     $request['positionSide'] = 'oneway';
                // } else {
                // $request = $this->omit($request, ['positionSide'])
                // }
            }
            if ($marginType) {
                $params = $this->omit($params, array( 'marginType' ));
                $request['marginMode'] = $marginType;
            }
            $cloid_suffix = 'r0';
            if ($reduceOnly) {
                $cloid_suffix = 'r1';
            }
            $request['clientOrderId'] = 'tealstreet' . $this->uuid16() . $cloid_suffix;
            $response = Async\await($this->$method (array_merge($request, $params)));
            $data = $this->safe_value($response, 'data', array());
            $first = $this->safe_value($data, 0);
            $order = $this->parse_order($first, $market);
            if (!$order['status']) {
                $code = $this->safe_string($first, 'code');
                if ($code === '0') {
                    $order['status'] = 'open';
                }
            }
            return array_merge(array_merge($request, $params), array_merge($order, array(
                'type' => $type,
                'side' => $side,
            )));
        }) ();
    }

    public function cancel_order($id, $symbol = null, $params = array ()) {
        return Async\async(function () use ($id, $symbol, $params) {
            /**
             * cancels an open order
             * @param {string} $id order $id
             * @param {string} $symbol unified $symbol of the market the order was made in
             * @param {array} $params extra parameters specific to the woo api endpoint
             * @return {array} An ~@link https://docs.ccxt.com/#/?$id=order-structure order structure~
             */
            if ($symbol === null) {
                throw new ArgumentsRequired($this->id . ' cancelOrder() requires a $symbol argument');
            }
            Async\await($this->load_markets());
            $type = $this->safe_string_lower($params, 'type');
            $isStop = $type === 'stop' || $type === 'stoplimit';
            if ($isStop) {
                return $this->cancel_algo_order($id, $symbol, $params);
            } else {
                return $this->cancel_regular_order($id, $symbol, $params);
            }
        }) ();
    }

    public function cancel_algo_order($id, $symbol = null, $params = array ()) {
        return Async\async(function () use ($id, $symbol, $params) {
            $request = array();
            $request['tpslId'] = $id;
            $market = null;
            if ($symbol !== null) {
                $market = $this->market($symbol);
            }
            $request['instId'] = $market['id'];
            $params = $this->omit($params, array( 'type' ));
            $request['clientOrderId'] = '';
            $response = Async\await($this->v1PrivatePostTradeCancelTpsl (array( array_merge($request, $params) )));
            //
            // array( success => true, status => 'CANCEL_SENT' )
            //
            $extendParams = array( 'symbol' => $symbol );
            $extendParams['id'] = $id;
            return array_merge($this->parse_order($response), $extendParams);
        }) ();
    }

    public function cancel_regular_order($id, $symbol = null, $params = array ()) {
        return Async\async(function () use ($id, $symbol, $params) {
            $request = array();
            $clientOrderIdUnified = $this->safe_string_2($params, 'clOrdID', 'clientOrderId');
            $clientOrderIdExchangeSpecific = $this->safe_string_2($params, 'clientOrderId', $clientOrderIdUnified);
            $isByClientOrder = $clientOrderIdExchangeSpecific !== null;
            if ($isByClientOrder) {
                $request['clientOrderId'] = $clientOrderIdExchangeSpecific;
                $params = $this->omit($params, array( 'clOrdID', 'clientOrderId', 'client_order_id' ));
            } else {
                $request['orderId'] = $id;
            }
            $market = null;
            if ($symbol !== null) {
                $market = $this->market($symbol);
            }
            $request['instId'] = $market['id'];
            $response = Async\await($this->v1PrivatePostTradeCancelOrder (array_merge($request, $params)));
            //
            // array( success => true, status => 'CANCEL_SENT' )
            //
            $extendParams = array( 'symbol' => $symbol );
            if ($isByClientOrder) {
                $extendParams['clientOrderId'] = $clientOrderIdExchangeSpecific;
            } else {
                $extendParams['instId'] = $id;
            }
            return array_merge($this->parse_order($response), $extendParams);
        }) ();
    }

    public function cancel_all_orders($symbol = null, $params = array ()) {
        return Async\async(function () use ($symbol, $params) {
            /**
             * cancel all open orders in a $market
             * @param {string|null} $symbol unified $market $symbol
             * @param {array} $params extra parameters specific to the woo api endpoint
             * @return {array} an list of ~@link https://docs.ccxt.com/#/?id=order-structure order structures~
