    public function fetch_account_configuration($symbol, $params = array ()) {
        return Async\async(function () use ($symbol, $params) {
            Async\await($this->load_markets());
            if ($symbol === 'BTC/USDT:USDT') {
                $symbol = 'BTC-USDT';
            }
            $market = $this->market($symbol);
            $leverageInfo = Async\await($this->fetch_leverage($market['id']));
            $leverage = $this->safe_integer($leverageInfo, 'leverage');
            $accountConfig = array(
                'marginMode' => 'cross',
                'positionMode' => 'oneway',
                'markets' => array(),
                'leverage' => $leverage,
            );
            $leverageConfigs = $accountConfig['markets'];
            $leverageConfigs[$market['symbol']] = array(
                'leverage' => $leverage,
                'buyLeverage' => $leverage,
                'sellLeverage' => $leverage,
            );
            return $accountConfig;
        }) ();
    }

    public function handle_errors($httpCode, $reason, $url, $method, $headers, $body, $response, $requestHeaders, $requestBody) {
        if (!$response) {
            return; // fallback to default $error handler
        }
        //
        //    {
        //        "code" => "1",
        //        "data" => array(
        //            array(
        //                "clOrdId" => "",
        //                "ordId" => "",
        //                "sCode" => "51119",
        //                "sMsg" => "Order placement failed due to insufficient balance. ",
        //                "tag" => ""
        //            }
        //        ),
        //        "msg" => ""
        //    ),
        //    {
        //        "code" => "58001",
        //        "data" => array(),
        //        "msg" => "Incorrect trade password"
        //    }
        //
        $code = $this->safe_string($response, 'code');
        if ($code !== '0') {
            $feedback = $this->id . ' ' . $body;
            $data = $this->safe_value($response, 'data', array());
            for ($i = 0; $i < count($data); $i++) {
                $error = $data[$i];
                $errorCode = $this->safe_string($error, 'sCode');
                $message = $this->safe_string($error, 'sMsg');
                $this->throw_exactly_matched_exception($this->exceptions['exact'], $errorCode, $feedback);
                $this->throw_broadly_matched_exception($this->exceptions['broad'], $message, $feedback);
            }
            $this->throw_exactly_matched_exception($this->exceptions['exact'], $code, $feedback);
            throw new ExchangeError($feedback); // unknown $message
        }
    }

    public function market($symbol) {
        $symbol = str_replace('/', '-', $symbol);
        // $symbol = $symbol . ':USDT';
        if ($this->markets === null) {
            throw new ExchangeError($this->id . ' markets not loaded');
        }
        if ($this->markets_by_id === null) {
            throw new ExchangeError($this->id . ' markets not loaded');
        }
        // TEALSTREET patch for backwards compatability
        // $this->market_helper(explode(':', $symbol)[0]);
        $foundMarket = $this->market_helper($symbol);
        if ($foundMarket) {
            return $foundMarket;
        }
        $marketStem = explode(':', $symbol)[0];
        $marketParts = explode('/', $marketStem);
        if (strlen($marketParts) === 2) {
            $foundMarket = $this->market_helper($marketParts[0] . '/' . $marketParts[1] . ':' . $marketParts[1]);
        }
        if ($foundMarket) {
            return $foundMarket;
        }
        $foundMarket = $this->market_helper($marketStem . ':USDT') || $this->market_helper($marketStem . ':BTC') || $this->market_helper($marketStem);
        if ($foundMarket) {
            return $foundMarket;
        }
        // eslint-disable-next-line no-console
        // var_dump ($symbol);
        // eslint-disable-next-line no-console
        // var_dump ($this->markets);
        throw new BadSymbol($this->id . ' does not have market $symbol ' . $symbol);
    }
