    public function parse_my_trades($trades, $market = null, $since = null, $limit = null, $params = array ()) {
        $result = array();
        for ($i = 0; $i < count($trades); $i++) {
            $filled = $this->safe_number($trades[$i], 'filledVolume', 0);
            if ($filled <= 0) {
                continue;
            }
            $action = $this->safe_string($trades[$i], 'action');
            if ($action === 'Close') {
                continue;
            }
            $trade = array_merge($this->parse_my_trade($trades[$i], $market), $params);
            $result[] = $trade;
        }
        $result = $this->sort_by($result, 'timestamp');
        $symbol = ($market !== null) ? $market['symbol'] : null;
        $tail = ($since === null);
        return $this->filter_by_symbol_since_limit($result, $symbol, $since, $limit, $tail);
    }

    public function parse_my_trade($trade, $market = null) {
        // {
        //     'orderId' => '1657690927243935744',
        //     'side' => 'Ask',
        //     'action' => 'Close',
        //     'tradeType' => 'Limit',
        //     'entrustVolume' => '0.0003',
        //     'entrustPrice' => '26337.6',
        //     'filledVolume' => '0',
        //     'avgFilledPrice' => '0',
        //     'entrustTm' => '2023-05-14T10:14:50Z',
        //     'symbol' => 'BTC-USDT',
        //     'profit' => '0',
        //     'commission' => '0',
        //     'updateTm' => '2023-05-14T10:14:50Z',
        //     'orderStatus' => 'Cancelled'
        // }
        $marketId = $this->safe_string($trade, 'symbol');
        $market = $this->safe_market($marketId);
        $symbol = $market['symbol'];
        $rawSide = $this->safe_string($trade, 'side');
        $id = $this->safe_string($trade, 'orderId');
        $action = $this->safe_string($trade, 'action');
        $type = $this->safe_string($trade, 'tradeType');
        $ePrice = $this->safe_string($trade, 'entrustPrice');
        $filled = $this->safe_string($trade, 'filledVolume');
        $avgFilledPrice = $this->safe_string($trade, 'avgFilledPrice');
        $entrustTm = $this->safe_string($trade, 'entrustTm');
        $timestamp = $this->parse_date($entrustTm);
        $profit = $this->safe_string($trade, 'profit');
        $commission = $this->safe_string($trade, 'commission');
        $status = $this->safe_string($trade, 'orderStatus');
        $isClose = false;
        if ($action === 'Close') {
            $isClose = true;
        }
        $side = null;
        if ($rawSide === 'Bid') {
            $side = 'buy';
        } else {
            $side = 'sell';
        }
        $takerOrMaker = null;
        $tradeType = $this->safe_string($trade, 'tradeType');
        if ($tradeType === 'Market') {
            $takerOrMaker = 'taker';
        } else {
            $takerOrMaker = 'maker';
        }
        $cost = 0.00045;
        if ($takerOrMaker === 'taker') {
            $cost = 0.00075;
        }
        return $this->safe_order(array(
            'info' => $trade,
            'id' => $id,
            'symbol' => $symbol,
            'side' => $side,
            'type' => $type,
            'filled' => $filled,
            'amount' => $filled,
            'average' => $avgFilledPrice,
            'takerOrMaker' => $takerOrMaker,
            'price' => $ePrice,
            'profit' => $profit,
            'fees' => $commission,
            'status' => $status,
            'timestamp' => $timestamp,
            'datetime' => $this->iso8601($timestamp),
            'isClose' => $isClose,
            'fee' => array(
                'currency' => $market['quote'],
                'cost' => $cost,
            ),
        ));
    }

    public function create_order($symbol, $type, $side, $amount, $price = null, $params = array ()) {
        /**
         * create a trade $order
         * @param {string} $symbol unified $symbol of the $market to create an $order in
         * @param {string} $type 'market' or 'limit'
         * @param {string} $side 'buy' or 'sell'
         * @param {float} $amount how much of currency you want to trade in units of base currency
         * @param {float|null} $price the $price at which the $order is to be fullfilled, in units of the quote currency, ignored in $market orders
         * @param {array} $params extra parameters specific to the paymium api endpoint
         * @return {array} an ~@link https://docs.ccxt.com/#/?id=$order-structure $order structure~
         */
        // quick $order:
        //
        // BTC/USDT:USDT
        // limit
        // buy
        // 4.0
        // 29116.0
        // array('positionMode' => 'unknown', 'timeInForce' => 'PO', 'reduceOnly' => False)
        //
        // limit $order:
        //
        // BTC/USDT:USDT
        // limit
        // buy
        // 4.0
        // 28520.0
        // array('positionMode' => 'unknown', 'timeInForce' => 'PO', 'reduceOnly' => False)
        //
        // no post = 'timeInForce' => 'GTC',
        //
        // SL
        //
        // BTC/USDT:USDT
        // stop
        // sell
        // 20.0
        // None
        // array('positionMode' => 'unknown', 'stopPrice' => 27663.0, 'timeInForce' => 'GTC', 'trigger' => 'Last', 'close' => True, 'basePrice' => 29024.0)
        //
        // TP
        //
        // BTC/USDT:USDT
        // stop
        // sell
        // 20.0
        // None
        // array('positionMode' => 'unknown', 'stopPrice' => 30150.0, 'timeInForce' => 'GTC', 'trigger' => 'Last', 'close' => True, 'basePrice' => 29024.0)
        //
        // LIMIT TP
        //
        // BTC/USDT:USDT
        // stopLimit
        // sell
        // 4.0
        // 33000.0
        // array('positionMode' => 'unknown', 'stopPrice' => 32000.0, 'timeInForce' => 'GTC', 'trigger' => 'Last', 'close' => True, 'basePrice' => 29024.0)
        $this->load_markets();
        $market = $this->market($symbol);
        //
        $triggerPrice = $this->safe_value_2($params, 'stopPrice', 'triggerPrice');
        $triggerType = $this->safe_string_lower_2($params, 'trigger', 'workingType');
        // $isTriggerOrder = $triggerPrice !== null;
        $isStopLossOrder = null;
        $isTakeProfitOrder = null;
        $reduceOnly = $this->safe_value_2($params, 'close', 'reduceOnly', false);
        $basePrice = $this->safe_value($params, 'basePrice');
        $positionSide = null;
        if (!$reduceOnly) {
            if ($side === 'buy') {
                $positionSide = 'LONG';
            } else {
                $positionSide = 'SHORT';
            }
        } else {
            if ($side === 'buy') {
                $positionSide = 'SHORT';
            } else {
                $positionSide = 'LONG';
            }
        }
        if ($triggerPrice !== null && $basePrice !== null) {
            // triggerOrder is NOT stopOrder
            // $type = 'market';
            if ($reduceOnly) {
                if ($side === 'buy') {
                    if ($triggerPrice > $basePrice) {
                        $isStopLossOrder = true;
                    } else {
                        $isTakeProfitOrder = true;
                    }
                } else {
                    if ($triggerPrice < $basePrice) {
                        $isStopLossOrder = true;
                    } else {
                        $isTakeProfitOrder = true;
                    }
                }
            } else {
                if ($side === 'buy') {
                    if ($triggerPrice < $basePrice) {
                        $isStopLossOrder = true;
                    } else {
                        $isTakeProfitOrder = true;
                    }
                } else {
                    if ($triggerPrice > $basePrice) {
                        $isStopLossOrder = true;
                    } else {
                        $isTakeProfitOrder = true;
                    }
                }
            }
        }
        //
        $convertedType = strtoupper($type);
        if ($type === 'stop') {
            if ($isTakeProfitOrder) {
                $convertedType = 'TRIGGER_MARKET';
            } elseif ($isStopLossOrder) {
                $convertedType = 'TRIGGER_MARKET';
            } else {
                throw new ArgumentsRequired('unknown $order direction for TP/SL');
            }
        }
        if ($type === 'stopLimit') {
            $convertedType = 'TRIGGER_LIMIT';
        }
        $convertedSide = strtoupper($side);
        $request = array(
            'symbol' => $market['id'],
            'type' => $convertedType,
            'side' => $convertedSide,
            'quantity' => $amount,
            'positionSide' => $positionSide,
        );
        if ($type === 'stop' || $type === 'stopLimit') {
            $triggerBy = 'MARK_PRICE';
            if ($triggerType === 'index') {
                $triggerBy = 'INDEX_PRICE';
            } elseif ($triggerType === 'last') {
                $triggerBy = 'CONTRACT_PRICE';
            }
            $request['workingType'] = $triggerBy;
        }
        if ($triggerPrice !== null) {
            $request['stopPrice'] = $this->price_to_precision($symbol, $triggerPrice);
            if ($convertedType === 'TRIGGER_LIMIT') {
                $request['price'] = $this->price_to_precision($symbol, $price);
            }
        } elseif ($triggerPrice === null && $convertedType === 'TRIGGER_LIMIT') {
            $request['price'] = $basePrice;
            $request['stopPrice'] = $basePrice;
        } elseif (($type === 'limit' || $type === 'stopLimit') && ($triggerPrice === null)) {
            $request['price'] = $this->price_to_precision($symbol, $price);
        }
        $isMarketOrder = $type === 'market';
        $exchangeSpecificParam = $this->safe_string_2($params, 'force', 'timeInForce');
        $postOnly = $this->is_post_only($isMarketOrder, $exchangeSpecificParam === 'PO', $params);
        if ($postOnly) {
            $request['timeInForce'] = 'PostOnly';
        }
        // $response = $this->swap2OpenApiPrivatePostSwapV2TradeOrder (array_merge($request, $params));
        $response = $this->swap2OpenApiPrivatePostSwapV2TradeOrder ($request);
        // var_dump('response', $response);
        $data = $this->safe_value($response, 'data');
        $order = $this->safe_value($data, 'order');
        // $parsedOrder = $this->parse_order($order, $market);
        // $patchedOrder = array_merge($parsedOrder, $params);
        // $patchedOrder = array_merge($patchedOrder, array(
        //     'price' => $price,
        //     'amount' => $amount,
        //     'side' => $side,
        //     'type' => $type,
        // ));
        return $this->parse_order($order, $market);
    }

    public function cancel_all_orders($symbol = null, $params = array ()) {
        /**
         * cancel all open orders in a $market
         * @see https://github.com/phemex/phemex-api-docs/blob/master/Public-Hedged-Perpetual-API.md#cancelall
         * @param {string} $symbol unified $market $symbol of the $market to cancel orders in
         * @param {array} $params extra parameters specific to the phemex api endpoint
         * @return {[array]} a list of ~@link https://docs.ccxt.com/#/?id=order-structure order structures~
         */
        if ($symbol === null) {
            throw new ArgumentsRequired($this->id . ' cancelAllOrders() requires a $symbol argument');
        }
        $this->load_markets();
        if ($symbol === null) {
            throw new ArgumentsRequired($this->id . ' cancelOrder() requires a $symbol argument');
        }
        $this->load_markets();
        $market = $this->market($symbol);
        $request = array(
            'symbol' => $market['id'],
        );
        $response = $this->swap2OpenApiPrivateDeleteSwapV2TradeAllOpenOrders ($request);
        return $response;
    }

    public function cancel_order($id, $symbol = null, $params = array ()) {
        /**
         * cancels an open order
         * @param {string} $id order $id
         * @param {string} $symbol unified $symbol of the $market the order was made in
         * @param {array} $params extra parameters specific to the bitget api endpoint
         * @return {array} An {@link https://docs.ccxt.com/en/latest/manual.html#order-structure order structure}
         */
        if ($symbol === null) {
            throw new ArgumentsRequired($this->id . ' cancelOrder() requires a $symbol argument');
        }
        $this->load_markets();
        $market = $this->market($symbol);
        $request = array(
            'symbol' => $market['id'],
            'orderId' => $id,
        );
        $response = $this->swap2OpenApiPrivateDeleteSwapV2TradeOrder ($request);
        return $this->parse_order($response, $market);
    }

    public function fetch_positions($symbols = null, $params = array ()) {
        /**
         * fetch all open $positions
         * @param {[string]|null} $symbols list of unified market $symbols
         * @param {array} $params extra parameters specific to the bybit api endpoint
         * @return {[array]} a list of ~@link https://docs.ccxt.com/#/?id=position-structure position structure~
         */
        $response = $this->swapV1PrivatePostUserGetPositions ();
        $data = $this->safe_value($response, 'data', array());
        $positions = $this->safe_value($data, 'positions', array());
        $result = array();
        for ($i = 0; $i < count($positions); $i++) {
            $result[] = $this->parse_position($positions[$i]);
        }
        return $result;
    }

    public function parse_position($position, $market = null) {
        //
        //
        // {
        //     "positionId" => "1650546544279240704",
        //     "symbol" => "BTC-USDT",
        //     "currency" => "",
        //     "volume" => 0.001,
        //     "availableVolume" => 0.001,
        //     "positionSide" => "short",
        //     "marginMode" => "cross",
        //     "avgPrice" => 27124.5,
        //     "liquidatedPrice" => 0.0,
        //     "margin" => 2.9386,
        //     "leverage" => 5.0,
        //     "pnlRate" => -45.83,
        //     "unrealisedPNL" => -2.4863,
        //     "realisedPNL" => 0.0126
        // }
        //
        $marketId = $this->safe_string($position, 'symbol');
        $market = $this->safe_market($marketId, $market);
        $timestamp = $this->safe_integer($position, 'cTime');
        $marginMode = $this->safe_string_lower($position, 'marginMode');
        $hedged = true;
        $side = $this->safe_string_lower($position, 'positionSide');
        $contracts = $this->safe_float($position, 'volume');
        $liquidation = $this->safe_number($position, 'liquidatedPrice');
        if ($side === 'short') {
            $contracts = -1 * $contracts;
        }
        if ($liquidation === 0) {
            $liquidation = null;
        }
        $initialMargin = $this->safe_number($position, 'margin');
        return array(
            'info' => $position,
            'id' => $market['symbol'] . ':' . $side,
            'symbol' => $market['symbol'],
            'notional' => null,
            'marginMode' => $marginMode,
            'liquidationPrice' => $liquidation,
            'entryPrice' => $this->safe_number($position, 'avgPrice'),
            'unrealizedPnl' => $this->safe_number_2($position, 'unrealisedPNL', 'unrealizedPL'),
            'realizedPnl' => $this->safe_number_2($position, 'realisedPNL', 'realizedPL'),
            'percentage' => null,
            'contracts' => $contracts,
            'contractSize' => $this->safe_number($market, 'contractSize'),
            'side' => $side,
            'hedged' => $hedged,
            'timestamp' => $timestamp,
            'markPrice' => $this->safe_number($position, 'markPrice'),
            'datetime' => $this->iso8601($timestamp),
            'maintenanceMargin' => null,
            'maintenanceMarginPercentage' => null,
            'collateral' => $this->safe_number($position, 'margin'),
            'initialMargin' => $initialMargin,
            'initialMarginPercentage' => null,
            'leverage' => $this->safe_number($position, 'leverage'),
            'marginRatio' => null,
        );
    }

    public function fetch_ohlcv($symbol, $timeframe = '1m', $since = null, $limit = null, $params = array ()) {
        /**
         * fetches historical candlestick data containing the open, high, low, and close price, and the volume of a $market
         * @see https://bybit-exchange.github.io/docs/v5/market/kline
         * @see https://bybit-exchange.github.io/docs/v5/market/mark-kline
         * @see https://bybit-exchange.github.io/docs/v5/market/index-kline
         * @see https://bybit-exchange.github.io/docs/v5/market/preimum-index-kline
         * @param {string} $symbol unified $symbol of the $market to fetch OHLCV data for
         * @param {string} $timeframe the length of time each candle represents
         * @param {int|null} $since timestamp in ms of the earliest candle to fetch
         * @param {int|null} $limit the maximum amount of candles to fetch
         * @param {array} $params extra parameters specific to the bybit api endpoint
         * @return {[[int]]} A list of candles ordered, open, high, low, close, volume
         */
        $this->check_required_symbol('fetchOHLCV', $symbol);
        $this->load_markets();
        $market = $this->market($symbol);
        $request = array(
            'symbol' => $market['id'],
        );
        if ($limit === null) {
            $limit = 200; // default is 340 when requested with `$since`
        }
        if ($since !== null) {
            $request['startTime'] = $since;
        }
        $klineType = $this->safe_string($this->timeframes, $timeframe, $timeframe);
        $request['interval'] = $timeframe;
        if ($limit !== null) {
            // $request['limit'] = $limit; // max 1000, default 1000
            if ($klineType === '1') {
                $request['endTime'] = $since . $limit * 60 * 1000;
            } elseif ($klineType === '3') {
                $request['endTime'] = $since . $limit * 3 * 60 * 1000;
            } elseif ($klineType === '5') {
                $request['endTime'] = $since . $limit * 5 * 60 * 1000;
            } elseif ($klineType === '15') {
                $request['endTime'] = $since . $limit * 15 * 60 * 1000;
            } elseif ($klineType === '30') {
                $request['endTime'] = $since . $limit * 30 * 60 * 1000;
            } elseif ($klineType === '60') {
                $request['endTime'] = $since . $limit * 60 * 60 * 1000;
            } elseif ($klineType === '120') {
                $request['endTime'] = $since . $limit * 120 * 60 * 1000;
            } elseif ($klineType === '240') {
                $request['endTime'] = $since . $limit * 240 * 60 * 1000;
            } elseif ($klineType === '360') {
                $request['endTime'] = $since . $limit * 360 * 60 * 1000;
            } elseif ($klineType === '720') {
                $request['endTime'] = $since . $limit * 720 * 60 * 1000;
            } elseif ($klineType === '1D') {
                $request['endTime'] = $since . $limit * 24 * 60 * 60 * 1000;
            } elseif ($klineType === '1W') {
                $request['endTime'] = $since . $limit * 7 * 24 * 60 * 60 * 1000;
            } elseif ($klineType === '1M') {
                $request['endTime'] = $since . $limit * 30 * 24 * 60 * 60 * 1000;
            } else {
                $request['endTime'] = $since . $limit * 60 * 1000;
            }
        }
        // var_dump ('===============');
        // var_dump ('fetchOHLCV', $symbol, $timeframe, $since, $limit, $params, $klineType);
        // var_dump ('now', +new Date (), new Date ());
        // var_dump ('startTs', +new Date ($request['startTime']), new Date ($request['startTime']));
        // var_dump ('endTs', +new Date ($request['endTime']), new Date ($request['endTime']));
        $response = $this->swap2OpenApiPublicGetSwapV2QuoteKlines (array_merge($request, $params));
        // var_dump ('lastCandleTs', strlen($response->data[$response->data) - 1] ? +new Date (strlen(+$response->data[$response->data) - 1].time) : 'none', strlen($response->data[$response->data) - 1] ? new Date (strlen(+$response->data[$response->data) - 1].time) : 'none');
        // var_dump ('response', $response);
        $ohlcvs = $this->safe_value($response, 'data', array());
        if (strlen($ohlcvs) > 0) {
            // BEGIN Patching last candle
            $lastRequest = $this->omit($request, array( 'startTime', 'endTime' ));
            $lastCandleResponse = $this->swap2OpenApiPublicGetSwapV2QuoteKlines (array_merge($lastRequest, $params));
            $lastOhlcv = $this->safe_value($lastCandleResponse, 'data', array());
            $lastOhlcvTime = $this->safe_integer($lastOhlcv, 'time');
            // var_dump('loht', $lastOhlcvTime, new Date ($lastOhlcvTime));
            $lastOhlcvFromArrayTime = $this->safe_integer(mb_substr($ohlcvs, -1), 'time');
            if ($lastOhlcvTime >= $lastOhlcvFromArrayTime) {
                $ohlcvs[] = $lastOhlcv;
            }
            // END Patching last candle
        }
        return $this->parse_ohlcvs($ohlcvs, $market, $timeframe, $since, $limit);
    }

    public function parse_ohlcvs($ohlcvs, $market = null, $timeframe = '1m', $since = null, $limit = null) {
        $results = array();
        for ($i = 0; $i < count($ohlcvs); $i++) {
            $results[] = $this->parse_ohlcv($ohlcvs[$i], $market);
        }
        $sorted = $this->sort_by($results, 0);
        $tail = ($since === null);
        return $this->filter_by_since_limit($sorted, $since, $limit, 0, $tail);
    }

    public function parse_ohlcv($ohlcv, $market = null) {
        return array(
            $this->safe_integer($ohlcv, 'time'), // timestamp
            $this->safe_number($ohlcv, 'open'), // open
            $this->safe_number($ohlcv, 'high'), // high
            $this->safe_number($ohlcv, 'low'), // low
            $this->safe_number($ohlcv, 'close'), // close
            $this->safe_number($ohlcv, 'volume'), // volume
        );
    }

    public function parse_order_status($status) {
        $statuses = array(
            'pending' => 'open',
            'new' => 'open',
            'partially_filled' => 'open',
            'filled' => 'closed',
            'canceled' => 'canceled',
            'pending_cancel' => 'canceling', // currently unused
            'rejected' => 'rejected',
            'expired' => 'expired',
        );
        return $this->safe_string($statuses, $status, $status);
    }

    public function parse_stop_trigger($status) {
        $statuses = array(
            'market_price' => 'mark', // unused
            'mark_price' => 'mark',
            'fill_price' => 'last', // unused
            'contract_price' => 'last',
            'index_price' => 'index', // unused
        );
        return $this->safe_string($statuses, $status, $status);
    }

    public function parse_order_type($type) {
        $types = array(
            'limit' => 'limit',
            'market' => 'market',
            'stop_market' => 'stop',
            'take_profit_market' => 'stop',
            'take_profit_limit' => 'stopLimit',
            'trigger_limit' => 'stopLimit',
            'trigger_market' => 'stop',
        );
        return $this->safe_string_lower($types, $type, $type);
    }

